#!/bin/bash
X=
X=$(df -g | awk 'NR < 3' | awk 'NR > 1' | awk '{ print $4}')
echo "Free disk space:" $X "GB"
if [ $X -gt 25 ]; then
  echo "Disk space is ok."
else
  echo "Low disk space detected"
  echo "Cleanup global workspace"
  rm -rf ~/workspace/*tests* && true
  rm -rf ~/workspace/*testapps* && true
  rm -rf ~/workspace/nativescript* && true
  rm -rf ~/worksapce/build-plugins-* && true
  rm -rf ~/workspace/composite* && true
  rm -rf ~/workspace/starter* && true
  rm -rf ~/workspace/live* && true
fi
