#!/bin/bas
set +e

# java
export JAVA_HOME="$(/usr/libexec/java_home -v 1.8)"

export PATH="/usr/local/bin:$PATH"

# node env variables
export PATH="/usr/local/opt/node@9/bin:$PATH"

# python
export PATH="/usr/local/opt/python/libexec/bin:$PATH"

export PATH="/usr/local/opt/gnu-tar/libexec/gnubin:$PATH"

# sikuli
export CLASSPATH=/Users/nsbuilduser/SikuliX/sikulixapi.jar

# ns-server
export USE_DEVICE_CONTROLLER_SERVER=true
export DEVICE_CONTROLLER_SERVER_PORT=8700
export MONGODB_STORAGE=$HOME/data/db

export MAX_EMU_COUNT=4
export MAX_SIM_COUNT=6

# aliases

alias l='ls -la'
alias g='cd ~/git'
alias ns'-'setup='cd ~/ns-setup'
alias data='cd ~/data'
alias workspace='cd ~/workspace'
alias jenkins='cd ~/jenkins'
alias keystore='cd ~/keystore'
alias b='cat ~/.bash_profile'

alias emulator='$ANDROID_HOME/emulator/emulator'
alias adb='$ANDROID_HOME/platform-tools/adb'
alias android='$ANDROID_HOME/tools/android'
alias avdmanager='$ANDROID_HOME/tools/bin/avdmanager'
alias sdkmanager='$ANDROID_HOME/tools/bin/sdkmanager'


